// frontend/src/UserForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';
const socket = io('http://localhost:5000');

function UserForm({ addFriend }) {
  const [name, setName] = useState('');
  const [hometown, setHometown] = useState('');
  const [mbti, setMbti] = useState('');
  const [hobby, setHobby] = useState('');

  useEffect(() => {
    socket.on('connect', () => {
      console.log('サーバーに接続しました');
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/users', { name, hometown, mbti, hobby });
      addFriend(response.data);
      socket.emit('join', response.data);
      setName('');
      setHometown('');
      setMbti('');
      setHobby('');
    } catch (error) {
      console.error('ユーザーの作成に失敗しました:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>名前:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <div>
        <label>出身地:</label>
        <input type="text" value={hometown} onChange={(e) => setHometown(e.target.value)} required />
      </div>
      <div>
        <label>MBTIタイプ:</label>
        <input type="text" value={mbti} onChange={(e) => setMbti(e.target.value)} required />
      </div>
      <div>
        <label>趣味:</label>
        <input type="text" value={hobby} onChange={(e) => setHobby(e.target.value)} required />
      </div>
      <button type="submit">送信</button>
    </form>
  );
}

export default UserForm;
