  // フレンドリストに友達を追加
  const addFriend = (friendData) => {
    setFriends((prevFriends) => [...prevFriends, friendData]);
  };

  return (
    <div>
      <h1>会話促進アプリへようこそ</h1>
      <UserForm addFriend={addFriend} />
    </div>
  );
}

export default App;
